import React, { useState } from "react";
import ToDoList from "./ToDoList";
const App = () => {
  let [item, setItem] = useState("");
  const [itemList, setList] = useState([]);
  const itemEvent = (event) => {
    setItem(event.target.value);
  };
  const onSubmit = () => {
    setList((preValue) => {
      return [...preValue, item];
    });
    setItem("");
  };
  return (
    <>
      <div className="min-h-screen grid place-items-center bg-sky-700">
        <div className="bg-gray-100 h-auto w-1/2 md:w-3/5 lg:w-2/5 rounded-lg shadow-sky-700/40">
          <h1 className="text-white text-center text-3xl font-bold mt-3 bg-fuchsia-800 mb-6 shadow-md">
            ToDoList
          </h1>
          <div className="flex flex-row items-center justify-center mb-8">
            <input
              className="bg-gray-100 outline-0 w-full md:w-4/6 text-center p-1 mb-2 md:mb-8 border-b-4 border-b-fuchsia-800"
              type="text"
              placeholder="Add items"
              value={item}
              onChange={itemEvent}
            />
            <button
              onClick={onSubmit}
              className="w-10 h-10 rounded-full bg-fuchsia-800 hover:bg-green-600 transition-all duration-300 text-center"
            >
              +
            </button>
          </div>
          <ul className="text-center text-fuchsia-800 text-lg font-serif">
            {/* <li>{item}</li> */}
            {itemList.map((itemVal) => {
              return <ToDoList text={itemVal} />;
            })}
          </ul>
        </div>
      </div>
    </>
  );
};

export default App;
