import React, { useState } from "react";
const App = () => {
  let [item, setItem] = useState("");
  const[itemList,setList] = useState([]);
  const itemEvent = (event) =>{
    setItem(event.target.value);
  };
  const onSubmit = () =>{
    setList((preValue)=>{
        return [...preValue,item];
    })
    setItem("");
  };
  return (
    <>
      <div className="h-screen grid place-items-center bg-sky-700">
        <div className="bg-gray-100 h-2/3 w-1/5 rounded-lg shadow-sky-700/40">
          <h1 className="text-white text-center text-3xl font-bold mt-3 bg-fuchsia-800 mb-6 shadow-md">
            ToDoList
          </h1>
          <input
            className="bg-gray-100 outline-0 w-4/6 text-center p-1 ml-3 mb-8 border-b-4 border-b-fuchsia-800"
            type="text"
            placeholder="Add items"
            value = {item}
            onChange={itemEvent}
          />
          <button onClick={onSubmit} className="ml-3 w-1/6">
            <i className="fa-solid fa-circle-plus text-fuchsia-800 text-5xl hover:text-green-600"></i>
          </button>
          <ul className="text-center text-fuchsia-800 text-lg font-serif">
            {/* <li>{item}</li> */}
            {itemList.map((itemVal) => {
                return(
                    <>
                        <li>{itemVal}</li>
                    </>
                );
            })
            }
          </ul>
        </div>
      </div>
    </>
  );
};

export default App;
